       }
       @media (max-width: 464px) { /* Show 
only on very small screens */
         .break-sm-hidden {
           display: block;
         }
       }

       .circles {
         position: absolute;
         bottom: -75px;
         left: 0;
         width: 100%;
         height: 100%;
         pointer-events: none;
         margin: 0; /* Remove default ul margin */
         padding: 0; /* Remove default ul padding */
         list-style: none; /* Remove default list bullets */
       }

       .circles li {
         position: absolute;
         display: block;
         background: rgba(0, 0, 0, 0.125);
         animation: animateCircle linear infinite;
       }

       /* Individual circle properties */
       .circles li:nth-child(1) { left: 10%; width: 20px; height: 20px; animation-delay: 0s; animation-duration: 15s; }
       .circles li:nth-child(2) { left: 5%; width: 15px; height: 15px; animation-delay: 2s; animation-duration: 10s; }
       .circles li:nth-child(3) { left: 60%; width: 15px; height: 15px; animation-delay: 4s; animation-duration: 15s; }
       .circles li:nth-child(4) { left: 30%; width: 40px; height: 40px; animation-delay: 0s; animation-duration: 18s; }
       .circles li:nth-child(5) { left: 50%; width: 15px; height: 15px; animation-delay: 0s; animation-duration: 15s; }
       .circles li:nth-child(6) { left: 75%; width: 60px; height: 60px; animation-delay: 3s; animation-duration: 15s; }
       .circles li:nth-child(7) { left: 35%; width: 75px; height: 75px; animation-delay: 7s; animation-duration: 15s; }
       .circles li:nth-child(8) { left: 50%; width: 15px; height: 15px; animation-delay: 15s; animation-duration: 45s; }
       .circles li:nth-child(9) { left: 20%; width: 10px; height: 10px; animation-delay: 2s; animation-duration: 35s; }
       .circles li:nth-child(10) { left: 85%; width: 75px; height: 75px; animation-delay: 0s; animation-duration: 11s; }
